import React from 'react';
import { Nav, NavDropdown } from 'react-bootstrap';
import { BsPhone, BsPerson, BsPersonFill } from 'react-icons/bs';

const CategoryMenu = () => {
  return (
    <Nav className="category-menu" style={{ backgroundColor: '#f8f9fa', border: '1px solid #dee2e6', borderRadius: '5px', padding: '10px' ,height: '400px' }}>
      <NavDropdown title={<span><BsPhone className="category-icon" /> Electronics</span>} id="electronics-menu">
        <NavDropdown.Item href="#electronics-smartphones">Smartphones</NavDropdown.Item>
        <NavDropdown.Item href="#electronics-laptops">Laptops</NavDropdown.Item>
        <NavDropdown.Item href="#electronics-accessories">Accessories</NavDropdown.Item>
      </NavDropdown>

      <NavDropdown title={<span><BsPerson className="category-icon" /> Men</span>} id="men-menu">
        <NavDropdown.Item href="#men-clothing">Clothing</NavDropdown.Item>
        <NavDropdown.Item href="#men-footwear">Footwear</NavDropdown.Item>
        <NavDropdown.Item href="#men-accessories">Accessories</NavDropdown.Item>
      </NavDropdown>

      <NavDropdown title={<span><BsPersonFill className="category-icon" /> Women</span>} id="women-menu">
        <NavDropdown.Item href="#women-clothing">Clothing</NavDropdown.Item>
        <NavDropdown.Item href="#women-footwear">Footwear</NavDropdown.Item>
        <NavDropdown.Item href="#women-accessories">Accessories</NavDropdown.Item>
      </NavDropdown>
    </Nav>
  );
};

export default CategoryMenu;
