import React, { useState, useEffect } from 'react';
import { Form, Button, Nav } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import FormContainer from '../components/FormContainer';
import CheckoutSteps from '../components/CheckoutSteps';
import { savePaymentMethod } from '../actions/cartActions';

function PaymentScreen({ history }) {
    const cart = useSelector(state => state.cart);
    const { shippingAddress } = cart;

    const dispatch = useDispatch();
    const [paymentMethod, setPaymentMethod] = useState('Carte de Crédit');

    useEffect(() => {
        if (!shippingAddress.address) {
            history.push('/shipping');
        }
    }, [shippingAddress, history]);

    const submitHandler = (e) => {
        e.preventDefault();
        dispatch(savePaymentMethod(paymentMethod));
        history.push('/placeorder');
    };

    return (
        <FormContainer>
            <CheckoutSteps step1 step2 step3 />

            <Form onSubmit={submitHandler}>
                <Form.Group>
                    <Form.Label as='legend'>Sélectionnez la méthode de paiement</Form.Label>

                    <Nav>
                        {/* Each Nav.Item is directly within the Nav element */}
                        <Nav.Item>
                            <Form.Check
                                type='radio'
                                label='Carte de Crédit'
                                id='creditCard'
                                name='paymentMethod'
                                checked={paymentMethod === 'Carte de Crédit'}
                                onChange={() => setPaymentMethod('Carte de Crédit')}
                            />
                        </Nav.Item>

                        <Nav.Item>
                            <Form.Check
                                type='radio'
                                label='Paiement à la Livraison (Livreur)'
                                id='livreur'
                                name='paymentMethod'
                                checked={paymentMethod === 'Livreur'}
                                onChange={() => setPaymentMethod('Livreur')}
                            />
                        </Nav.Item>
                    </Nav>
                </Form.Group>

                <Button type='submit' variant='primary'>
                    Continuer
                </Button>
            </Form>
        </FormContainer>
    );
}

export default PaymentScreen;
