import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Row, Col, Button, Dropdown } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import Product from '../components/Product';
import Loader from '../components/Loader';
import Message from '../components/Message';
import Paginate from '../components/Paginate';
import ProductCarousel from '../components/ProductCarousel';
import { listProducts } from '../actions/productActions';

function HomeScreen({ history }) {
  const dispatch = useDispatch();
  const productList = useSelector((state) => state.productList);
  const { error, loading, products, page, pages } = productList;

  let keyword = history.location.search;


  const [selectedCategory, setSelectedCategory] = useState('');
  const [selectedSubcategory, setSelectedSubcategory] = useState('');
  const [categories, setCategories] = useState([
    {
      name: 'Electronics',
      subcategories: ['Smartphones', 'Laptops', 'Accessories'],
    },
    {
      name: 'Clothing',
      subcategories: ['Men', 'Women', 'Kids'],
    },
    // Add more categories and subcategories as needed
  ]);




  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
    setSelectedSubcategory(''); // Reset subcategory when a category is selected
  };

  const handleSubcategorySelect = (subcategory) => {
    setSelectedSubcategory(subcategory);
    // You may want to dispatch an action to filter products by subcategory here
  };
  return (
    <div className="home-screen">
      {!keyword && <ProductCarousel />}




      <div className="category-menu">
        <h2>Explore Categories</h2>
        <Dropdown onSelect={handleCategorySelect}>
          <Dropdown.Toggle variant="success" id="category-dropdown">
            {selectedCategory || 'Select Category'}
          </Dropdown.Toggle>
          <Dropdown.Menu>
            {categories.map((category, index) => (
              <Dropdown.Item key={index} eventKey={category.name}>
                {category.name}
              </Dropdown.Item>
            ))}
          </Dropdown.Menu>
        </Dropdown>

        {selectedCategory && (
          <Dropdown onSelect={handleSubcategorySelect}>
            <Dropdown.Toggle variant="info" id="subcategory-dropdown">
              {selectedSubcategory || 'Select Subcategory'}
            </Dropdown.Toggle>
            <Dropdown.Menu>
              {categories
                .find((category) => category.name === selectedCategory)
                .subcategories.map((subcategory, index) => (
                  <Dropdown.Item key={index} eventKey={subcategory}>
                    {subcategory}
                  </Dropdown.Item>
                ))}
            </Dropdown.Menu>
          </Dropdown>
        )}
      </div>

      <div className="latest-products">
        <h1>Latest Products</h1>
        {loading ? (
          <Loader />
        ) : error ? (
          <Message variant="danger">{error}</Message>
        ) : products.length === 0 ? (
          <Message>No products found.</Message>
        ) : (
          <Row>
            {products.map((product) => (
              <Col key={product._id} sm={12} md={6} lg={4} xl={3}>
                <Product product={product} />
              </Col>
            ))}
          </Row>
        )}
        <Paginate page={page} pages={pages} keyword={keyword} />
      </div>


    </div>
  );
}

export default HomeScreen;
