import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Link } from 'react-router-dom'
import { Carousel, Image } from 'react-bootstrap'
import Loader from './Loader'
import Message from './Message'
import { listTopProducts } from '../actions/productActions'
// ... (existing imports)

function ProductCarousel() {
    const dispatch = useDispatch();
    const productTopRated = useSelector(state => state.productTopRated);
    const { error, loading, products } = productTopRated;

    useEffect(() => {
        dispatch(listTopProducts());
    }, [dispatch]);

    return (
        loading ? (
            <Loader />
        ) : error ? (
            <Message variant='danger'>{error}</Message>
        ) : (
            <Carousel pause='hover' className='bg-dark' interval={5000}>
                {products.map(product => (
                    <Carousel.Item key={product._id} className="d-flex align-items-center">
                        <div className="w-50">
                            <Link to={`/product/${product._id}`}>
                                <Image src={product.image} alt={product.name} fluid className="img-fluid" />
                            </Link>
                        </div>
                        <div className="w-50 text-left pl-4">
                            <Link to={`/product/${product._id}`}>
                                <h4>{product.name}</h4>
                                <p>${product.price}</p>
                            </Link>
                        </div>
                    </Carousel.Item>
                ))}
            </Carousel>
        )
    );
}

export default ProductCarousel;
