import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { Carousel, Image } from 'react-bootstrap';
import Loader from './Loader';
import Message from './Message';
import { listTopProducts } from '../actions/productActions';
// ... (existing imports)

function ProductCarousel() {
    const dispatch = useDispatch();
    const productTopRated = useSelector(state => state.productTopRated);
    const { error, loading, products } = productTopRated;

    const localImageContext = require.context('../images', false, /\.(jpg|jpeg|png)$/);
    const localImagePaths = localImageContext.keys();

    useEffect(() => {
        dispatch(listTopProducts());
    }, [dispatch]);

    return (
        loading ? (
            <Loader />
        ) : error ? (
            <Message variant='danger'>{error}</Message>
        ) : (
            <Carousel pause='hover' interval={5000}>
                {products.map(product => (
                    <Carousel.Item key={product._id}>
                        <Link to={`/product/${product._id}`} className="d-block">
                            {product.image.startsWith('../images') ? (
                                <Image src={localImageContext(product.image).default} alt={product.name} fluid className="img-fluid rounded" />
                            ) : (
                                <Image src={product.image} alt={product.name} fluid className="img-fluid rounded" />
                            )}
                        </Link>
                    </Carousel.Item>
                ))}
            </Carousel>
        )
    );
}

export default ProductCarousel;
