import React, { useState, useEffect } from 'react';
import { Button, Row, Col, ListGroup, Image, Card, Modal } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import Message from '../components/Message';
import CheckoutSteps from '../components/CheckoutSteps';
import { createOrder } from '../actions/orderActions';
import { ORDER_CREATE_RESET } from '../constants/orderConstants';

function PlaceOrderScreen({ history }) {
    const [showConfirmation, setShowConfirmation] = useState(false);

    const orderCreate = useSelector(state => state.orderCreate);
    const { order, error, success } = orderCreate;

    const dispatch = useDispatch();
    const cart = useSelector(state => state.cart);

    // Calcul du total, frais de livraison et taxes
    cart.itemsPrice = cart.cartItems.reduce((acc, item) => acc + item.price * item.qty, 0).toFixed(2);
    cart.shippingPrice = (cart.itemsPrice > 100 ? 0 : 10).toFixed(2);
    cart.taxPrice = Number((0.082) * cart.itemsPrice).toFixed(2);
    cart.totalPrice = (Number(cart.itemsPrice) + Number(cart.shippingPrice) + Number(cart.taxPrice)).toFixed(2);

    // Redirection vers la page de paiement si aucune méthode de paiement n'est sélectionnée
    if (!cart.paymentMethod) {
        history.push('/payment');
    }

    useEffect(() => {
        // Redirection vers la page de commande en cas de succès
        if (success) {
            // Afficher la popup de confirmation lorsque la commande est réussie
            setShowConfirmation(true);
            dispatch({ type: ORDER_CREATE_RESET });
        }
    }, [success, dispatch]);

    const handleClose = () => setShowConfirmation(false);

    // Fonction pour placer la commande
    const placeOrder = () => {
        dispatch(createOrder({
            orderItems: cart.cartItems,
            shippingAddress: cart.shippingAddress,
            paymentMethod: cart.paymentMethod,
            itemsPrice: cart.itemsPrice,
            shippingPrice: cart.shippingPrice,
            taxPrice: cart.taxPrice,
            totalPrice: cart.totalPrice,
        }));
    };

    return (
        <div>
            {/* Affichage des étapes de la commande */}
            <CheckoutSteps step1 step2 step3 step4 />
            <Row>
                <Col md={8}>
                    <ListGroup variant='flush'>
                        {/* ... (Autres détails de la commande) */}
                    </ListGroup>
                </Col>

                <Col md={4}>
                    <Card>
                        <ListGroup variant='flush'>
                            {/* ... (Autres détails de la commande) */}

                            <ListGroup.Item>
                                {/* Bouton de placement de commande */}
                                <Button
                                    type='button'
                                    className='btn btn-primary btn-block'
                                    disabled={cart.cartItems.length === 0 || orderCreate.loading}
                                    onClick={placeOrder}
                                >
                                    Passer la Commande
                                </Button>
                            </ListGroup.Item>
                        </ListGroup>
                    </Card>
                </Col>
            </Row>

            {/* Confirmation Modal */}
            <Modal show={showConfirmation} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Commande Confirmée</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <p>Merci pour votre commande! Votre numéro de commande est {order._id}.</p>
                    <p>Vous recevrez une confirmation par e-mail.</p>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="primary" onClick={handleClose}>
                        Fermer
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
}

export default PlaceOrderScreen;
