import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Link } from 'react-router-dom'
import { Carousel, Image } from 'react-bootstrap'
import Loader from './Loader'
import Message from './Message'
import { listTopProducts } from '../actions/productActions'
// ... (existing imports)

function ProductCarousel() {
    const dispatch = useDispatch();
    const productTopRated = useSelector(state => state.productTopRated);
    const { error, loading, products } = productTopRated;

    useEffect(() => {
        dispatch(listTopProducts());
    }, [dispatch]);

    return (
        loading ? (
            <Loader />
        ) : error ? (
            <Message variant='danger'>{error}</Message>
        ) : (
            <Carousel pause='hover' className='bg-dark' interval={5000}>
                {products.map(product => (
                    <Carousel.Item key={product._id} className="d-flex align-items-center">
                        <Link to={`/product/${product._id}`} className="d-flex align-items-center w-100">
                            <div className="text-left mr-3 text-light">
                                <h4>{product.name}</h4>
                                <p>${product.price}</p>
                            </div>
                            <Image src={product.image} alt={product.name} fluid className="img-fluid rounded" />
                        </Link>
                    </Carousel.Item>
                ))}
            </Carousel>
        )
    );
}

export default ProductCarousel;
