import React, { useState, useEffect } from 'react';
import { Nav, NavDropdown } from 'react-bootstrap';

const CategoryMenu = () => {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    // Fetch data from Django backend
    const fetchData = async () => {
      try {
        const response = await fetch('/api/categories');  // Update this URL based on your Django setup
        const data = await response.json();
        setCategories(data);
      } catch (error) {
        console.error('Error fetching category data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <Nav className="category-menu">
      {categories.map((category) => (
        <NavDropdown title={<span className="category-title">{category.title}</span>} key={category.id}>
          {category.items.map((item) => (
            <NavDropdown.Item href={`#${item.slug}`} key={item.id}>
              {item.name}
            </NavDropdown.Item>
          ))}
        </NavDropdown>
      ))}
    </Nav>
  );
};

export default CategoryMenu;
