import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import { listProductsByType } from '../actions/productActions';
import Loader from '../components/Loader';
import Message from '../components/Message';
import Product from '../components/Product';

const TypeScreen = () => {
    const { type } = useParams();
    const dispatch = useDispatch();

    const productList = useSelector((state) => state.productList);
    const { loading, error, products } = productList;

    useEffect(() => {
        dispatch(listProductsByType(type));
    }, [dispatch, type]);

    return (
        <div>
            <h2>{`Products with type: ${type}`}</h2>

            {loading && <Loader />}

            {error && <Message variant="danger">{error}</Message>}

            {!loading && !error && products.length === 0 && (
                <Message>No products found for this type.</Message>
            )}

            {!loading && !error && products.length > 0 && (
                <div>
                {products
            .filter(product => product.category.type.toLowerCase() === type.toLowerCase()) // Case-insensitive filter
            .map((filteredProduct) => (
                <Product key={filteredProduct._id} product={filteredProduct} />
            ))}     
                </div>
            )}
        </div>
    );
};

export default TypeScreen;
