// CategoryMenu.js

import React from 'react';
import { Nav, NavDropdown } from 'react-bootstrap';
import { BsPhone, BsPerson, BsPersonFill } from 'react-icons/bs';


const CategoryMenu = () => {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('/api/categories');
        setCategories(response.data);
      } catch (error) {
        console.error('Error fetching categories:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <Nav className="category-menu">
      {categories.map((category, index) => (
        <NavDropdown key={index} title={<span className="category-title">{category.title}</span>} id={`${category.title.toLowerCase()}-menu`}>
          <NavDropdown.Item>
            <img src={category.icon} alt={category.title} className="category-icon" />
          </NavDropdown.Item>
          {/* Render other subcategories or additional information here */}
        </NavDropdown>
      ))}
    </Nav>
  );
};

export default CategoryMenu;
