import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Link } from 'react-router-dom'
import { Carousel, Image } from 'react-bootstrap'
import Loader from './Loader'
import Message from './Message'
import { listTopProducts } from '../actions/productActions'
// ... (existing imports)


function ProductCarousel() {
   // Dynamically import all images from the 'public/images' folder
   const imageContext = require.context('../public/images', false, /\.(jpg|jpeg|png)$/);
   const imagePaths = imageContext.keys();

   return (
     <div>
       <h2>List of Images</h2>
       <div className="image-list">
         {imagePaths.map((imagePath, index) => (
           <img key={index} src={imageContext(imagePath).default} alt={`Image ${index}`} />
         ))}
       </div>
     </div>
   );
 }

export default ProductCarousel;
