import React, { useState, useEffect } from 'react';
import { Button, Row, Col, ListGroup, Image, Card } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import Message from '../components/Message';
import CheckoutSteps from '../components/CheckoutSteps';
import { createOrder } from '../actions/orderActions';
import { ORDER_CREATE_RESET } from '../constants/orderConstants';

function PlaceOrderScreen({ history }) {
    const orderCreate = useSelector(state => state.orderCreate);
    const { order, error, success } = orderCreate;

    const dispatch = useDispatch();
    const cart = useSelector(state => state.cart);

    // Calcul du total, frais de livraison et taxes
    cart.itemsPrice = cart.cartItems.reduce((acc, item) => acc + item.price * item.qty, 0).toFixed(2);
    cart.shippingPrice = (cart.itemsPrice > 100 ? 0 : 10).toFixed(2);
    cart.taxPrice = Number((0.082) * cart.itemsPrice).toFixed(2);
    cart.totalPrice = (Number(cart.itemsPrice) + Number(cart.shippingPrice) + Number(cart.taxPrice)).toFixed(2);

    // Redirection vers la page de paiement si aucune méthode de paiement n'est sélectionnée
    if (!cart.paymentMethod) {
        history.push('/payment');
    }

    useEffect(() => {
        // Redirection vers la page de commande en cas de succès
        if (success) {
            history.push(`/order/${order._id}`);
            dispatch({ type: ORDER_CREATE_RESET });
        }
    }, [success, history]);

    // Fonction pour placer la commande
    const placeOrder = () => {
        dispatch(createOrder({
            orderItems: cart.cartItems,
            shippingAddress: cart.shippingAddress,
            paymentMethod: cart.paymentMethod,
            itemsPrice: cart.itemsPrice,
            shippingPrice: cart.shippingPrice,
            taxPrice: cart.taxPrice,
            totalPrice: cart.totalPrice,
        }));
    };

    return (
        <div>
            {/* Affichage des étapes de la commande */}
            <CheckoutSteps step1 step2 step3 step4 />
            <Row>
                <Col md={8}>
                    <ListGroup variant='flush'>
                        <ListGroup.Item>
                            <h2>Adresse de Livraison</h2>
                            <p>
                                <strong>Adresse: </strong>
                                {cart.shippingAddress.address}, {cart.shippingAddress.city} {' '}
                                {cart.shippingAddress.postalCode}, {' '}
                                {cart.shippingAddress.country}
                            </p>
                        </ListGroup.Item>

                        <ListGroup.Item>
                            <h2>Méthode de Paiement</h2>
                            <p>
                                <strong>Méthode: </strong>
                                {cart.paymentMethod}
                            </p>
                        </ListGroup.Item>

                        <ListGroup.Item>
                            <h2>Articles de Commande</h2>
                            {cart.cartItems.length === 0 ? (
                                <Message variant='info'>
                                    Votre panier est vide
                                </Message>
                            ) : (
                                <ListGroup variant='flush'>
                                    {cart.cartItems.map((item, index) => (
                                        <ListGroup.Item key={index}>
                                            <Row>
                                                <Col md={2}>
                                                    <Image src={item.image} alt={item.name} fluid rounded />
                                                </Col>

                                                <Col md={4}>
                                                    <Link to={`/product/${item.product}`}>{item.name}</Link>
                                                </Col>

                                                <Col md={6}>
                                                    {item.qty} X {item.price}DT = {(item.qty * item.price).toFixed(2)} DT
                                                </Col>
                                            </Row>
                                        </ListGroup.Item>
                                    ))}
                                </ListGroup>
                            )}
                        </ListGroup.Item>
                    </ListGroup>
                </Col>

                <Col md={4}>
                    <Card>
                        <ListGroup variant='flush'>
                            <ListGroup.Item>
                                <h2>Récapitulatif de la Commande</h2>
                            </ListGroup.Item>

                            <ListGroup.Item>
                                <Row>
                                    <Col>Articles:</Col>
                                    <Col>{cart.itemsPrice}DT</Col>
                                </Row>
                            </ListGroup.Item>

                            <ListGroup.Item>
                                <Row>
                                    <Col>Livraison:</Col>
                                    <Col>{cart.shippingPrice}DT</Col>
                                </Row>
                            </ListGroup.Item>

                            <ListGroup.Item>
                                <Row>
                                    <Col>Taxes:</Col>
                                    <Col>{cart.taxPrice}DT</Col>
                                </Row>
                            </ListGroup.Item>

                            <ListGroup.Item>
                                <Row>
                                    <Col>Total:</Col>
                                    <Col>{cart.totalPrice}DT</Col>
                                </Row>
                            </ListGroup.Item>

                            <ListGroup.Item>
                                {/* Affichage des messages d'erreur */}
                                {error && <Message variant='danger'>{error}</Message>}
                            </ListGroup.Item>

                            <ListGroup.Item>
                                {/* Bouton de placement de commande */}
                                <Button
                                    type='button'
                                    className='btn btn-primary btn-block'
                                    disabled={cart.cartItems.length === 0 || orderCreate.loading}
                                    onClick={placeOrder}
                                >
                                    Passer la Commande
                                </Button>
                            </ListGroup.Item>
                        </ListGroup>
                    </Card>
                </Col>
            </Row>
        </div>
    );
}

export default PlaceOrderScreen;
